import { useEffect, useState } from "react";
import "./UserCourses.css";
import { useLocation } from "react-router-dom";
import axios from "axios";

interface Course {
  id: number;
  courseId: number;
  lastCompletedDay: string | null;
  attempts: number;
  isCompleted: boolean;
  courseTitle: string;
  courseDescription: string;
}

export default function UserCourses() {
  const location = useLocation();
  const userId = location.state?.userId;

  const [courses, setCourses] = useState<Course[]>([]);

  useEffect(() => {
    if (!userId) {
      console.error("No user ID provided!");
      return;
    }

    const fetchCourses = async () => {
      try {
        const response = await axios.get(`http://localhost:5117/api/usercourse/${userId}`);
        
        console.log("API Response:", response.data);
        
        const courseData = Array.isArray(response.data) ? response.data : [response.data];
        setCourses(courseData);
      } catch (error) {
        console.error("Error fetching user courses:", error);
        setCourses([]);
      }
    };

    fetchCourses();
  }, [userId]);

  return (
    <div className="table-container">
      <h2 className="text-xl font-bold mb-4">User Courses</h2>
      {userId ? (
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
              <th>Attempts</th>
              <th>Completed</th>
              <th>Last Completed</th>
            </tr>
          </thead>
          <tbody>
            {courses.map((course) => (
              <tr key={course.id}>
                <td>{course.courseTitle}</td>
                <td>{course.courseDescription}</td>
                <td>{course.attempts}</td>
                <td style={{ color: course.isCompleted ? "green" : "red", fontSize: "18px" }}>
  {course.isCompleted ? "✔️" : "❌"}
</td>
                <td>{course.lastCompletedDay ? new Date(course.lastCompletedDay).toLocaleDateString() : "Never"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-red-500">No user data available.</p>
      )}
    </div>
  );
  
}
