import { useEffect, useState } from "react";
import axios from "axios";
import "./UserCourses.css";

interface Course {
  id: number;
  courseId: number;
  lastCompletedDay: string | null;
  attempts: number;
  isCompleted: boolean;
  courseTitle: string; // Endret fra `title`
  courseDescription: string;
}

interface Props {
  userId: number;
}

export default function UserCourses({ userId }: Props) {
  const [courses, setCourses] = useState<Course[]>([]);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get(`http://localhost:5117/api/usercourse/${userId}`);
        
        console.log("API Response:", response.data); // üîç Debugging
        
        // Wrap single object in an array
        const courseData = Array.isArray(response.data) ? response.data : [response.data];
        
        setCourses(courseData);
      } catch (error) {
        console.error("Error fetching user courses:", error);
        setCourses([]); // Prevents `.map()` errors
      }
    };
  
    fetchCourses();
  }, [userId]);
  

  return (
    <table className="table-auto w-full text-left">
    <thead>
      <tr>
        <th className="px-4 py-2">Title</th>
        <th className="px-4 py-2">Description</th>
        <th className="px-4 py-2">Attempts</th>
        <th className="px-4 py-2">Completed</th>
        <th className="px-4 py-2">Last Completed</th>
      </tr>
    </thead>
    <tbody>
      {courses.map((course) => (
        <tr key={course.id} className="border-t">
          <td className="px-4 py-2">{course.courseTitle}</td>
          <td className="px-4 py-2">{course.courseDescription}</td>
          <td className="px-4 py-2">{course.attempts}</td>
          <td className="px-4 py-2">{course.isCompleted ? "Yes" : "No"}</td>
          <td className="px-4 py-2">
            {course.lastCompletedDay ? new Date(course.lastCompletedDay).toLocaleDateString() : "Never"}
          </td>
        </tr>
      ))}
    </tbody>
  </table>
  
  );
}
