import { useEffect, useState } from "react";
import axios from "axios";
import './U'

interface Course {
  id: number;
  courseId: number;
  lastCompletedDay: string | null;
  attempts: number;
  isCompleted: boolean;
  title: string;
  description: string;
}

interface Props {
  userId: number;
}

export default function UserCourses({ userId }: Props) {
  const [courses, setCourses] = useState<Course[]>([]);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get(`http://localhost:5117/api/usercourse/${userId}`);
        
        console.log("API Response:", response.data); // üîç Debugging
        
        // Wrap single object in an array
        const courseData = Array.isArray(response.data) ? response.data : [response.data];
        
        setCourses(courseData);
      } catch (error) {
        console.error("Error fetching user courses:", error);
        setCourses([]); // Prevents `.map()` errors
      }
    };
  
    fetchCourses();
  }, [userId]);
  

  return (
    <div className="courses-container">
      <h3>Your Courses</h3>
      {courses.length === 0 ? (
        <p>No courses yet.</p>
      ) : (
        <ul>
          {courses.map((course) => (
            <li key={course.id}>
              <h4>{course.title}</h4>
              <p>{course.description}</p>
              <p><strong>Attempts:</strong> {course.attempts}</p>
              <p><strong>Completed:</strong> {course.isCompleted ? "Yes" : "No"}</p>
              <p><strong>Last Completed:</strong> {course.lastCompletedDay ? new Date(course.lastCompletedDay).toLocaleDateString() : "Never"}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
