import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Courses.css";
import { useUserProgress } from "../UserProgressContext";

interface Course {
    courseId: number;
    description: string;
    title: string
}

interface ApiResponse<T> {
  success: boolean;
  message: string | null;
  data: T;
}

export default function Courses() {
  const { completedUnits } = useUserProgress(); // –ë–µ—Ä—ë–º –¥–∞–Ω–Ω—ã–µ –∏–∑ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞
  const [courses, setCourses] = useState<Course[]>([]);
  const navigate = useNavigate();
  const [pageIndex, setPageIndex] = useState(1);
const pageSize = 10;
const [hasMore, setHasMore] = useState(true);

const fetchCourses = async (page: number) => {
  try {
    const response = await axios.get<ApiResponse<Course[]>>(`http://localhost:5117/api/learning/courses`, {
      params: { pageIndex: page, pageSize },
    });

    if (response.data.success) {
      setCourses((prev) => [...prev, ...response.data.data.items]); // Append new courses
      if (response.data.data.length < pageSize) {
        setHasMore(false); // No more courses to load
      }
    }
  } catch (error) {
    console.error("Error fetching courses: ", error);
  }
};

useEffect(() => {
  fetchCourses(pageIndex);
}, []);

const loadMoreCourses = () => {
  const nextPage = pageIndex + 1;
  setPageIndex(nextPage);
  fetchCourses(nextPage);
};


    return (
      <div className="courses-container">
        <h2>Available Courses</h2>
        <div className="courses-grid">
          {courses.map((course, index) => {
            const isLocked = index > completedUnits;
    
            return (
              <div
                key={course.courseId}
                className={`course-card ${isLocked ? "locked" : ""}`}
                onClick={() => !isLocked && navigate(`/courses/${course.courseId}`)}
              >
                {isLocked && <span className="lock-icon">üîí</span>}
                <div className="course-content">
                  <div className="course-index">Unit {index + 1}</div>
                  <div className="course-title">{course.title}</div>
                  <div className="course-description">{course.description}</div>
                </div>
              </div>
            );
          })}
        </div>
    
        {hasMore && (
          <button onClick={loadMoreCourses} className="show-more-button">
            Show More
          </button>
        )}
      </div>
    );
    
  
}
