import React, { useState } from "react";

interface EditUserFormProps {
  id: number;
  fullName: string;
  email: string;
  onUpdate: (updatedUser: { id: number; fullName: string; email: string }) => void;
  onCancel: () => void;
}

export default function EditUserForm({ id, fullName, email, onUpdate, onCancel }: EditUserFormProps) {
  const [name, setName] = useState(fullName);
  const [userEmail, setUserEmail] = useState(email);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onUpdate({ id, fullName: name, email: userEmail });
  };

  return (
    <div >
    <h2>Edit User</h2>
          <form onSubmit={handleSubmit} className="edit-user-form">
      <div>
        <label>Full Name:</label>
        <input value={name} onChange={(e) => setName(e.target.value)} required />
      </div>
      <div>
        <label>Email:</label>
        <input type="email" value={userEmail} onChange={(e) => setUserEmail(e.target.value)} required />
      </div>
      <button type="submit">Save</button>
      <button type="button" onClick={onCancel}>Cancel</button>
    </form>
    </div>

  );
}
