import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Courses.css";
import { useUserProgress } from "../UserProgressContext";

interface Course {
    courseId: number;
    description: string;
    title: string
}

interface ApiResponse<T> {
  success: boolean;
  message: string | null;
  data: T;
}

export default function Courses() {
  const { completedUnits } = useUserProgress(); // Берём данные из контекста
  const [courses, setCourses] = useState<Course[]>([]);
  const navigate = useNavigate();
  const [page, setPage] = useState(1);
  const pageSize = 10; // Количество курсов на странице
  const [totalPages, setTotalPages] = useState(1);

const fetchCourses = async () => {
  try {
    const response = await axios.get("http://localhost:5117/api/learning/courses", {
      params: { pageIndex: page, pageSize }
    });

    console.log("API Response:", response.data); // Проверка формата ответа

    setCourses(response.data.data.items);
    setTotalPages(response.data.data.totalPages); // Сохраняем общее количество страниц
  } catch (error) {
    console.error("Error fetching courses: ", error);
  }
};

useEffect(() => {
  fetchCourses();
}, [page]);



    return (
<div className="courses-container">
      {/* Pagination ABOVE Courses */}
      <div className="pagination">
        <button onClick={() => setPage(1)} disabled={page === 1}>First</button>
        <button onClick={() => setPage((prev) => Math.max(prev - 1, 1))} disabled={page === 1}>Previous</button>

        {[...Array(totalPages)].map((_, i) => (
          <button
            key={i + 1}
            onClick={() => setPage(i + 1)}
            className={page === i + 1 ? "active" : ""}
          >
            {i + 1}
          </button>
        ))}

        <button onClick={() => setPage((prev) => Math.min(prev + 1, totalPages))} disabled={page === totalPages}>Next</button>
        <button onClick={() => setPage(totalPages)} disabled={page === totalPages}>Last</button>
      </div>

      {/* Courses List */}
      <h2>Available Courses</h2>
      <div className="courses-grid">
        {courses.map((course, index) => (
          <div key={course.courseId} className="course-card" onClick={() => navigate(`/courses/${course.courseId}`)}>
            {/* FIX: Course Numbering is Now Continuous */}
            <div className="course-index">Unit {index + 1 + (page - 1) * pageSize}</div>
            <div className="course-title">{course.title}</div>
            <div className="course-description">{course.description}</div>
          </div>
        ))}
      </div>
      </div>
  );
}
